package com.caicongyang.client.jar;

import com.alibaba.fastjson.JSON;
import com.caicongyang.client.OdfsUploadClient;
import com.caicongyang.client.domain.UploadResult;
import com.ksyun.ks3.utils.Base64;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;

/**
 * @author am
 * @date 2020-07-29
 */
public class OdfsTools {

    static String fileBase64 = "";
    static String command = "upload";

    public static void main(String[] args) throws Exception {

        File file = null;

        System.out.println("该 jar 调用方式为 \n"
                + "\tjava -jar **.jar  [ C:/1234.jpg | /tmp/1234.png ]\n"
                + "默认 global.config.path 路径为：[ C:/data/env/ | /data/env/ ]， 请确保配置了 env.ini. ");

        if (args != null && args.length > 0) {
            String fileName = args[0].trim();
            file = new File(fileName);
            if (!file.exists()) {
                throw new RuntimeException(fileName + "，该路径下文件不存在！");
            }
        } else {
            file = base64ToFile(fileBase64);
        }

        String global_config_path = System.getProperty("global.config.path");
        String osNameString = System.getProperty("os.name");

        if (global_config_path == null || "".equals(global_config_path)) {
            if (osNameString.contains("Windows"))
                System.setProperty("global.config.path", "C:/data/env");
            else {
                System.setProperty("global.config.path", "/data/env");
            }
        }

        System.out.println("global_config_path --> " + global_config_path);
        System.out.println("before getInstance");
        OdfsUploadClient uploadClient = OdfsUploadClient.getInstanceFromConfig();

        System.out.println("after getInstance");
        UploadResult result = null;
        try {
            if (command.equals("upload")) {
                result = uploadClient.upload(file, "osoa");
                if (result.getFailCount() == 0) {
                    System.out.println("上传成功！访问链接为：" + result.getResultDetail().get(0).getUrl());
                } else {
                    System.out.println("上传失败！");
                }
                System.out.println("上传完整结果：" + JSON.toJSONString(result));

            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public static File base64ToFile(String base64) {
        if (base64 == null || "".equals(base64)) {
            return null;
        }
        byte[] buff = Base64.decode(base64);
        File file = null;
        FileOutputStream fout = null;
        try {
            file = File.createTempFile("tmp", ".png");
            fout = new FileOutputStream(file);
            fout.write(buff);
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            if (fout != null) {
                try {
                    fout.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
        return file;
    }
}